class Solution {
public:
    int minimumOperations(vector<int>& nums, int start, int goal) {
        unordered_map<int, bool> vis;
        queue<int> q;
        q.push(start);
        int ans = 0;
        while(!q.empty()) 
        {
            int size = q.size();

            while(size--) {
                int node = q.front();
                q.pop();
                if(node == goal) return ans;
                if(node < 0 || node > 1000 || vis[node]) {
                    continue;
                }
                vis[node] = true;
                for(int i = 0; i < nums.size(); i++) {
                    q.push(node + nums[i]);
                    q.push(node - nums[i]);
                    q.push(node ^ nums[i]);
                }
            }
            ans++;
        }
        return -1;
    }
};
