class Solution {
public:
    int halveArray(vector<int>& nums) {
        int cnt=0;
        priority_queue<double>que;

        double sum=0;
        for(auto i:nums){
            sum+=i;
            que.push(i);
        }
        double temp=sum;

        while(temp>sum/2.0){
           double t=que.top()/2.0;
            que.pop();
            temp-=t;
            cout<<t<<" ";
            if(t!=0.0)
                que.push(t);
            cnt++;
        }

        return cnt;



    }
};
