class Solution {
public:
    int minOperations(int n) {

       vector<int> v;
        int sum = 0;
        for(int i = 0; i < n; i++) {
            v.push_back(2 * i + 1);
        }

        int i = 0;
        int j = n - 1;
        while(i <= j) {
            sum += (v[j] - v[i]) / 2;

            i++;
            j--;
        } 
        return sum;
    }
};
