class Solution {
public:
    int minOperations(vector<int>& nums) {
        if(nums.size() == 1) return 0;
        int cnt = 0;
        for(int i=1;i<nums.size();i++){

            while(nums[i] <= nums[i-1]){
               int x = abs(nums[i] - nums[i-1]) + 1;
                nums[i] += x;
                cnt += x;
            }

        }

        return cnt;
    }
};
