class Solution {
public:
    int makeTheIntegerZero(int num1, int num2) {
        if(num1 < num2)return -1;
        for (int i = 0; i <= 100; i++) {  
            long long d = num1 - 1LL * (num2) * i;  
            int b = __builtin_popcountll(d);  
            if (b <= i && i <= d) 
                return i;
        }
        return -1; 
    }
};
