class Solution {
public:
    int minOperations(int n){
        if (pow(2, floor(log2(n * 1.0))) == n)
            return 1;

        long long cur = floor(log2(n));
        long long hi = pow(2, cur + 1);
        long long lo = pow(2, cur);

        return min(1 + minOperations(abs(n - hi)), 1 + minOperations(abs(n - lo)));
    }
};
