class Solution {
public:
    int minimumRecolors(string blocks, int k) {
        int n= blocks.length();
        int i = 0, j = k - 1;
        int cnt = 0;
        for(int i = 0; i < k;i++){
            if(blocks[i] =='W')cnt++;
        }
        int ans = cnt;
        while(j < blocks.size() - 1){
            j++;
            if(blocks[j] == 'W')cnt++;
            if(blocks[i] == 'W')cnt--;
            i++;
            ans = min(ans, cnt);
        }
        return ans;
    }
};
