class Solution {
public:
    string minRemoveToMakeValid(string s) {

        stack<int> st;
        for(int i = 0;i<s.length();i++) {
            if(s[i] == '(') {
                st.push(i);
            }

            else if(s[i] == ')') {
                if(!st.empty()) {
                    st.pop();
                }
                else {
                   s.erase(s.begin() + i);
                    i--;
                }
            }
        }
        while(!st.empty()) {
            int x = st.top();
            s.erase(s.begin() + x);
            st.pop();
        }

        return s;
}

};
