class Solution {
public:
    long long minimumReplacement(vector<int>& nums) {
        long long n = nums.size();
        long long prev = nums[n - 1];
        long long ans = 0;
        for(long long i = n - 2; i >= 0; i--) {
            long long times = nums[i] / prev;
            if((nums[i] % prev) != 0) {
                times++;
                prev = nums[i] / times;
            } 
            ans += times - 1;
        }

        return ans;
    }
};
