class Solution {
public:
    void dfs(vector<vector<pair<int, int>>> &adj, vector<int> &vis, int curr, int &ans){
        vis[curr] = 1;
        for(auto i : adj[curr]){
            ans = min(ans, i.second);
            if(vis[i.first])continue;
            dfs(adj, vis, i.first, ans);
        }
    }
    int minScore(int n, vector<vector<int>>& roads) {
        vector<vector<pair<int, int>>> adj(n + 2);
        for(auto i : roads){
            adj[i[0]].push_back({i[1], i[2]});
            adj[i[1]].push_back({i[0], i[2]});
        }
        int ans = 1e9;
        vector<int> vis(n + 2, 0);
        dfs(adj, vis, 1, ans);
        return ans;
    }
};
