class Solution {
public:
    vector<int> minSubsequence(vector<int>& nums) {

        sort(nums.begin(),nums.end(),greater<int>());
      int sum = 0;
        for(int i=0;i<nums.size();i++){
            sum += nums[i];
        }

        int ans = 0;
        vector<int> v;
        for(int i=0;i<nums.size();i++){
            ans += nums[i];
            v.push_back(nums[i]);

            if(ans > sum - ans) break;

        }

        return v;

    }
};
