class Solution {
public:
    int minimumSum(int num) {
        string s1, s2;
        string n = to_string(num);
        sort(n.begin(), n.end());
        for(int i = 0;i<n.length();i+=2){
            s1.push_back(n[i]);
            if(i + 1 < n.length())
                s2.push_back(n[i+1]);            
        }
        int n1 = stoi(s1);
        int n2 = stoi(s2);
        return n1 + n2;

    }
};
