bool cmpr(vector<int> &a, vector<int> &b){
    if(a[0] == b[0])return a[1] > b[1];
    return a[0] < b[0];
}
class Solution {
public:
    int findMinimumTime(vector<vector<int>>& tasks) {
        int n = tasks.size();
        sort(tasks.begin(), tasks.end(), [](vector<int> &a, vector<int> &b){
            return a[1] < b[1];
        });
        vector<int> arr(2005, 0);
        for(auto it : tasks){
            for(int i = it[0];i<=it[1] && it[2] > 0;i++){
                if(arr[i])it[2]--;
            }
            for(int i = it[1];i >=0 && it[2] > 0;i--){
                if(arr[i] == 0) {
                    arr[i] = 1;
                    it[2]--;
                }
            }
        }
        return accumulate(arr.begin(), arr.end(), 0);
    }
};
