class Solution {
public:
    long long minimumTime(vector<int>& time, int totalTrips) {
        sort(time.begin(), time.end());
        long long lo = 0, hi = 1LL * time[0] * totalTrips;
        long long ans = 1LL * time[0] * totalTrips;
        while(lo < hi){
            long long mid = lo + (hi - lo)/2;
            long long total = 0;
            for(auto i : time){
                total += mid/i;
            }

            if(total >= totalTrips){
                ans = mid;
                hi = mid;
            }else{
                lo = mid + 1;
            }
        }
        return ans;
    }
};
