class Solution {
public:
    int minimumFinishTime(vector<vector<int>>& tires, int ct, int nl) {
        vector<int> times(19,INT_MAX);
        for(auto iter:tires){
            int time_till_end=0;
            for(int i=1;i<=nl;i++){
                long long k=iter[0]*pow(iter[1],i-1);
                time_till_end+=k;
                if(k>pow(2,17)){
                    break;
                }
                times[i]=min(times[i],time_till_end);
            }

        }
        long long dp[nl+1];
        for(int i=1;i<=nl;i++){
            dp[i]=(i<19)?times[i]:INT_MAX;
            for(int j=1;j<=i;j++){
                dp[i]=min(dp[i],dp[j]+ct+dp[i-j]);
            }
        }
        return dp[nl];
    }
};
