class Solution {
public:

    int minimumTime(string s) {
        int n = s.length();
        vector<int> prefix(n, 0);
        vector<int> suffix(n, 0);

        if(s[0] == '1') prefix[0] = 1;
        for(int i = 1; i < n; i++) {
            if(s[i] == '1') {
            prefix[i] = min(prefix[i-1] + 2, i + 1); 
            }
            else {
                prefix[i] = prefix[i-1];

            }
        }
        if(s[n-1] == '1') suffix[n-1] = 1;
        for(int i = n - 2; i>= 0; i--) {
            if(s[i] == '1') {
            suffix[i] = min(suffix[i+1] + 2, n - i); 
            }
            else {
                suffix[i] = suffix[i+1];

            }
        }
        int ans = min(prefix[n-1], suffix[0]);
        for(int i = 1; i < n - 1; i++) {
            ans = min(ans, prefix[i] + suffix[i+1]);
        }
        return ans;
    }
};
