typedef long long int ll;
class Solution {
public:
    long long repairCars(vector<int>& ranks, int cars) {
        ll n = ranks.size();
        ll lo = 0, hi = 1e18;
        ll ans = 0;
        while (lo < hi)
        {
            ll mid = lo + (hi -lo)/2;
            ll cnt = 0;
            for (ll i = 0; i < n; i++)
            {
                cnt += floor(sqrt(mid / ranks[i]));
            }
            if (cnt >= cars)
                ans = mid,
                hi = mid;
            else
                lo = mid + 1;
        }
        return hi;
    }
};
