class Solution {
public:
    int minStartValue(vector<int>& nums) {

        int cs = 0;
        int mini = INT_MAX;
        // vector<int> cs;
        // cs.push_back(nums[0]);
        for(int i = 0;i<nums.size();i++){
            cs += nums[i];
            mini = min(mini, cs);
        }

        if(mini < 0) return -mini + 1;

        return 1;

    }
};
