class Solution {
public:
    int dp[1000+1][1000+1];
    long long helper(string &f,int nc,int &len,int i){
        if(i>f.length()-1) return 0;
        if(dp[nc][i]!=-1) return  dp[nc][i];
        if(nc>0){
            long long ans;
                if(i+len>f.length()-1){
                    ans=f.length()-i;
                }else ans=len;

            ans+=helper(f,nc-1,len,i+len);
            ans=max(ans,f[i]-'0'+helper(f,nc,len,i+1));
            return dp[nc][i]=ans;
        }
         // cout<<f[i]<<" ";
        return dp[nc][i]=(f[i]-'0'+helper(f,0,len,i+1));
    }
    int minimumWhiteTiles(string f, int nc, int len) {
        string p;
        for(auto i:f){
            if(i=='0') p.push_back('1');
            else p.push_back('0');
        }
        // cout<<p<<endl;
        memset(dp,-1,sizeof(dp));
        return f.length()-helper(p,nc,len,0);

    }
};
