class Solution
{
public:
    string minWindow(string s, string t)
    {
        unordered_map<char, int> umap;
        for (char c : t)
        {
            umap[c]++;
        }
        int i = 0, j = 0;
        int count = umap.size();
        string ans = "";
        int n = s.length();

        while (j < n)
        {
            if (count == 0)
            {
                string temp = s.substr(i, j - i);
                if (ans == "" || ans.length() > temp.length())
                {
                    ans = temp;
                }
                if (umap.find(s[i]) != umap.end())
                {
                    if (umap[s[i]] == 0)
                    {
                        count++;
                    }
                    umap[s[i]]++;
                }
                i++;
            }
            else
            {
                if (umap.find(s[j]) != umap.end())
                {
                    umap[s[j]]--;
                    if (umap[s[j]] == 0)
                    {
                        count--;
                    }
                }
                j++;
            }
        }
        while (count == 0)
        {
            if (umap.find(s[i]) != umap.end())
            {
                if (umap[s[i]] == 0)
                {
                    count++;
                    string temp = s.substr(i, j);
                    if (ans == "" || ans.length() > temp.length())
                    {
                        ans = temp;
                    }
                }
                umap[s[i]]++;
            }
            i++;
        }
        return ans;
    }
};
