class Solution {
public:
    bool isMonotonic(vector<int>& nums) {
        bool inc = true;


        for(int i = 1; i < nums.size(); i++) {
            if(nums[i - 1] > nums[i]) {
                inc = false;
                break;
            }
        }

        if(inc) return true;

        for(int i = 1; i < nums.size(); i++) {
            if(nums[i - 1] < nums[i]) return false;
        }
        return true;
    }
};
