class Solution {
public:
    string get(string &s){
        string t = "";
        for(auto i : s)t.push_back(tolower(i));
        return t;
    }
    string mostCommonWord(string paragraph, vector<string>& banned) {
        string temp = "";
        map<string, int> mp;
        for(auto i : paragraph){
            if(i == ' ' || i == '!' ||i == '?' || i == '\'' ||i == ','||i == ';' ||i == '.'){
                if(temp != ""){
                    mp[get(temp)]++;
                }
                temp = "";
            }else{
                temp.push_back(i);
            }
        }
        if(temp != ""){
            mp[get(temp)]++;
        }
        int _max = 0;
        string ans = "";
        map<string, int> ban;
        for(auto i : banned)ban[i]++;
        for(auto i : mp){
            if(ban[i.first] > 0)continue;
            if(i.second > _max){
                ans = i.first;
                _max = i.second;
            }
        }
        return ans;
    }
};
