class Solution {
public:
    int mostFrequentEven(vector<int>& nums) {
        map<int, int> mp;
        for(auto i : nums){
            mp[i]++;
        }
        int ans = -1;
        for(auto i : mp){
            if(i.first%2 == 0){
                if(mp[ans] < i.second){
                    ans = i.first;
                }
            }
        }
        return ans;
    }
};
