class Solution {
public:
    int mostFrequent(vector<int>& nums, int key) {
        unordered_map<int,int> umap;
        for(int i=0;i<nums.size()-1;i++){
            if(nums[i]==key){
                umap[nums[i+1]]++;
            }
        }
        int ans=0;
        int mn=0;
        for(auto iter:umap){
            if(mn<iter.second){
                mn=iter.second;
                ans=iter.first;
            }
        }
        return ans;
    }
};
