class Solution {
public:
    vector<vector<string>> mostPopularCreator(vector<string>& creators, vector<string>& ids, vector<int>& views) {
        map<string, long long> mp;
        long long n = creators.size();
        map<string, vector<pair<long long, string>>> arr;
        for(int i = 0; i < n; i++){
            mp[creators[i]] += views[i];
            arr[creators[i]].push_back({views[i], ids[i]});
        }
        vector<vector<string>> ans;
        long long _max = 0;
        for(auto i : mp)_max= max(_max, i.second);
        for(auto i : mp){
            if(i.second == _max){
                sort(arr[i.first].begin(), arr[i.first].end(), [&](pair<long long, string>&a, pair<long long, string>&b){
                    if(a.first == b.first){
                        return a.second < b.second;
                    }
                    return a.first > b.first;
                });
                ans.push_back({i.first, arr[i.first][0].second});
            }
        }
        return ans;
    }
};
