class Solution {
public:
    int vis[1001];
    unordered_map<int,vector<int>> ver,hor;
    int helper(vector<vector<int>>& stones,int ii){
        if(vis[ii]==0) return 0;
        int ans=0;
        ans+=vis[ii];
        vis[ii]=0;
        for(auto j:ver[stones[ii][1]]){
            auto i=stones[j];
            if(i[0]==stones[ii][0]||i[1]==stones[ii][1]) 
                ans+=helper(stones,j);
        }
        for(auto j:hor[stones[ii][0]]){
            auto i=stones[j];
            if(i[0]==stones[ii][0]||i[1]==stones[ii][1]) 
                ans+=helper(stones,j);
        }
        return ans;
    }
    int removeStones(vector<vector<int>>& stones) {
        for(int i=0;i<stones.size();i++){
            vis[i]++;
            hor[stones[i][0]].push_back(i);
            ver[stones[i][1]].push_back(i);
        }
        int ans=0;
         for(int i=0;i<stones.size();i++){
            if(vis[i]!=0){
                ans+=helper(stones,i)-1;
            }
        }
        return ans;

    }
};
