class Solution {
public:
    string multiply(string num1, string num2) {
        if(num1 =="0" || num2 == "0"){
            return "0";
        }
        vector<int> arr1(110, 0), arr2(110, 0), ans(10010, 0);
        for (int i = 0; i < num1.length(); i++)
        {
            arr1[i] = num1[num1.length() - i - 1] - '0';
        }
        for (int i = 0; i < num2.length(); i++)
        {
            arr2[i] = num2[num2.length() - i - 1] - '0';
        }
        for (int i = 0, j = 0; i < num1.size(); i++)
        {
            int carry = 0;
            vector<int> temp(10010, 0);
            //multiplication.
            for (j = 0; j < num2.size(); j++)
            {
                temp[i + j] = (arr2[j] * arr1[i]) + carry;
                carry = temp[i + j] / 10;
                temp[i + j] %= 10;
            }
            while (carry > 0)
            {
                temp[i + j] = carry % 10;
                carry /= 10;
                j++;
            }
            //addition.
            for (int k = 0; k < 10010; k++)
            {
                ans[k] = ans[k] + temp[k] + carry;
                carry = ans[k] / 10;
                ans[k] %= 10;
            }
        }
        bool found = false;
        string s;
        for (int i = 10010 - 1; i >= 0; i--)
        {
            if (!found && ans[i] != 0)
            {
                found = true;
            }
            if (found)
            {
                s += to_string(ans[i]);
            }
        }
        return s;
    }
};
