class Solution {
public:
    int repeatedNTimes(vector<int>& A) {
        map<int,int> m;
        for(auto i : A){
            m[i] ++;
        }
        int ans;
        for(auto i : m){
            if(i.second > 1){
              ans = i.first;
                break;
            }
        }
        return ans ;
    }
};
