class Solution {
public:
    vector<int> nextGreaterElement(vector<int>& nums1, vector<int>& nums2) {
        int n = nums2.size();
        vector<int> arr(n, -1);
        stack<int>st;
        for(int i = 0; i < n;i++){
            while(!st.empty() && nums2[st.top()] < nums2[i]){
                arr[st.top()] = nums2[i];
                st.pop();
            }
            st.push(i);
        }
        map<int, int> m;
        for(int i = 0; i < n;i++)m[nums2[i]] = i;

        vector<int> ans;
        for(auto i : nums1){
            ans.push_back(arr[m[i]]);
        }
        return ans;
    }
};
