class Solution {
public:
    vector<int> secondGreaterElement(vector<int>& nums) {
        stack<int> st1, st2, temp;
        int n = nums.size();
        vector<int> ans(n, -1);
        for(int i = 0; i < n; i++) {
            while (st2.size() > 0 and nums[st2.top()] <nums[i] ) {
                ans[st2.top()] = nums[i];
                st2.pop();
            }
            while (st1.size() > 0 and nums[st1.top()] < nums[i]) {
                temp.push(st1.top());
                st1.pop();
            }
            while (temp.size() > 0) {
                st2.push(temp.top());
                temp.pop();
            }
            st1.push(i);
        }
        return ans;
    }
};
