class Solution {
public:
    int edgeScore(vector<int>& edges) {
        int n = edges.size();
        vector<vector<int>> adj(n);
        for(int i = 0; i < n; i++){
            adj[edges[i]].push_back(i);
        }
        long long int _max = -1e09, ans = -1;
        for(long long int i = 0; i < n; i++){
            long long int sum = 0;
            for(auto it : adj[i]){
                sum += it;
            }
            if(sum > _max){
                ans = i;
            }
            _max = max(_max, sum);
        }
        return ans;
    }
};
