class Solution {
public:

    void helper(set<vector<int>> &ans, vector<int> &temp, vector<int> &nums, int ind, int prev){
        if(ind == nums.size()){
            if(temp.size() > 1)
                ans.insert(temp);
            return;
        }
        helper(ans, temp, nums, ind + 1, prev);
        if(nums[ind] >= prev){
            temp.push_back(nums[ind]);
            helper(ans, temp, nums, ind + 1, nums[ind]);
            temp.pop_back();
        }
    }
    vector<vector<int>> findSubsequences(vector<int>& nums) {
        set<vector<int>> ans;
        vector<int> temp;
        helper(ans, temp, nums, 0, -200);
        vector<vector<int>> res;
        for(auto i : ans)res.push_back(i);
        return res;
    }
};
