class Solution {
public:
   int findNthDigit(int n)
    {
        long long dig = 1;
        long long curr = 0;
        long long num = 0;
        while (1)
        {
            long long total = (pow(10, dig) - 1) - (pow(10, dig - 1) - 1);
            if (curr + total * dig >= n)
            {
                long long number = num + (n - curr - 1) / dig + 1;
                long long d = (n - curr - 1) % dig;
                string s = to_string(number);
                return s[d] - '0';
            }
            else
            {
                num += total;
                curr += total * dig;
                dig++;
            }
        }
        return -1;
    }

};
