        vector<int> color(n, 0);
        int ans = 0;
        vector<int> arr;
        for(auto i : queries){
            if(color[i[0]] == i[1]){

            }else{
                if(color[i[0]] != 0){
                    if(i[0] > 0 && color[i[0] - 1] == color[i[0]]){
                        ans--;
                    }
                    if(i[0] < n - 1 && color[i[0] + 1] ==  color[i[0]]){
                        ans--;
                    }
                }
                if(i[0] > 0 && color[i[0] - 1] == i[1]){
