class Solution {
public:
    int arithmeticTriplets(vector<int>& nums, int diff) {
        int n =  nums.size();
        map<int, int> cnt1, cnt2;
        for(int i =0 ; i < n; i++) {
            cnt2[nums[i]]++;
        }
        int ans = 0;
        for(int i = 0; i <  n; i++) {
            cnt2[nums[i]]--;
            ans += (cnt1[nums[i] - diff]) * (cnt2[nums[i] + diff]);
            cnt1[nums[i]]++;
        }

        return ans;
    }
};
