class Solution {
public:
    int countBeautifulPairs(vector<int>& nums) {
        int ans = 0;
        int n = nums.size();
        for(int i = 0; i < n;i++){
            for(int j = i + 1;  j < n;j++){
                string a = to_string(nums[i]);
                string b = to_string(nums[j]);
                if(__gcd(a[0] - '0', b.back() - '0') == 1)ans++;
            }
        }
        return ans;

    }
};
