class Solution {
public:
    map<int, map<int, int> > dp;
    const int N = (int) 1e9 + 7;
    int numRollsToTarget(int n, int k, int target) {
        if(n == 0) {
            if(target == 0) {
                return 1;
            }
            return 0;
        }
        if(target < 0) {
            return 0;
        }
        if(dp.count(n) > 0 and dp[n].count(target) > 0) return dp[n][target];
        int ans = 0;

        for(int i = 1; i <= k; i++) {
            ans += numRollsToTarget(n - 1, k, target - i)%N;
            ans %= N;
        }
        return dp[n][target] = ans;
    }
};
