class Solution {
public:
    vector<int> evenOddBit(int n) {
        int even = 0, odd = 0;
        for(int i = 0; i < 32;i++){
            int bit = ((n & ( 1<<i))) == 0 ?0:1;
            if(i%2 == 0){
                if(bit == 1)even++;
            }else{
                if(bit == 1)odd++;
            }
        }
        return {even, odd};
    }
};
