class Solution {
public:
    int numSplits(string s) {
        int n = s.size();

        int cnt[n + 1][27];
        memset(cnt, 0, sizeof cnt);
        for(int i = 0; i < n; i++) {
            cnt[i][s[i] - 'a']++;
        }
        for(int i = 1; i < n; i++) {
            for(int j = 0; j < 26; j++){
                cnt[i][j] += cnt[i - 1][j];
            }
        }

            int ans = 0;
            for(int i = 0; i < n - 1; i++) {
                int left = 0;
                int right = 0 ;
                for(int j = 0; j < 26; j++) {
                    left += (cnt[i][j] > 0);
                    right += (cnt[n - 1][j] - cnt[i][j] >0);
                }
                if(left == right) 
                {
                    ans++;
                }

            }

        return ans;

    }
};
