class Solution {
public:
    vector<int> numberOfLines(vector<int>& widths, string s) {
        int curr = 0;
        int lines = 1;
        for(auto i : s){
            if(widths[i - 'a'] + curr > 100){
                lines++;
                curr = widths[i-'a'];
            }else{
                curr += widths[i-'a'];
            }
        }
        return {lines, curr};
    }
};
