class Solution {
public:

    int dfs(vector<vector<int>> &adj, vector<int> &vis, int curr, int prev){
        vis[curr] = 1;
        int ans = 0;
        for(auto i : adj[curr]){
            if(i == prev)continue;
            if(vis[i])ans++;
            else ans += dfs(adj, vis, i, curr);
        }
        return ans;
    }
    int makeConnected(int n, vector<vector<int>>& connections) {
        vector<vector<int>> adj(n + 1);
        for(auto i : connections){
            adj[i[0]].push_back(i[1]);
            adj[i[1]].push_back(i[0]);
        }
        int loops = 0;
        int components = 0;
        vector<int> vis(n + 1, 0);
        for(int i = 0; i < n;i++){
            if(vis[i])continue;
            components++;
            loops += dfs(adj, vis, i, -1);
        }
        if(components == 1)return 0;
        if(loops >= components)return components - 1;
        return -1;
    }
};
