class Solution {
public:
    int countGoodRectangles(vector<vector<int>>& v) {

        vector<int> vec;
        for(int i=0;i<v.size();i++){
            vec.push_back(min(v[i][0],v[i][1]));
        }
        sort(vec.begin(),vec.end());
       int cnt = 0;
        int x = vec[vec.size()-1];
       for(int i=vec.size()-1;i>=0;i--){
           if(vec[i] != x) break;
           cnt++;
       }
        return cnt;
        }
};
