class Solution {
public:
    int countSegments(string s) {
        if(s == "")return 0;
        string temp = "";
        int ans = 0;
        for(auto i : s){
            if(i == ' '){
                if(temp != ""){
                    ans++;
                    temp = "";
                }
            }
            else{
                temp.push_back(i);
            }
        }
        if(temp != "")ans++;
        return ans;
    }
};
