class Solution {
public:
    int countStudents(vector<int>& students, vector<int>& sandwiches) {


        int i=0;
        int j = 0;
        while(i < students.size()){
            if(students[i] == sandwiches[j]){
                i++;
                j++;
            }

            else {
                students.push_back(students[i]);
                i++;
                int sum = 0;
                for(int k = i;k<students.size();k++){
                    sum += students[k];
                }

                if(sandwiches[j] == 0 and sum == students.size() - i){
                    return sum;
                }

                else if(sandwiches[j] == 1 and sum == 0){
                    return  students.size() - i;
                }
            }
        }

        return 0;
    }
};
