class Solution {
public:
    const int MOD = 1000000007;
    int numOfSubarrays(vector<int>& arr) {
        for(int i = 1; i < arr.size(); i++) {
            arr[i] += arr[i - 1];
        }

        map<int, int> m;
        m[0] = 1;
        int ans = 0;
        for(int i = 0; i < arr.size(); i++) {
            if(arr[i]&1) {
                ans += m[0];
            }
            else {
                ans += m[1];
            }
            ans %= MOD;
            m[arr[i]%2]++;

        }
        return ans;
    }
};
