class Solution {
public:
    int subarrayLCM(vector<int>& nums, int k) {
        int ans =0 ;
        for(int i = 0; i < nums.size(); i++){
            int prod = 1, g =0;
            for(int j = i; j < nums.size(); j++){
                prod = (prod * nums[j])/(__gcd(prod, nums[j]));
                if(prod == k)ans++;
                if(prod > k)break;
            }
        }
        return ans;
    }
};
