class Solution {
public:
    typedef long long int ll;
    const int MOD = 1e9 + 7;
    ll mul(ll a, ll b){
        return ((a%MOD)*(b%MOD))%MOD;
    }
    ll power(ll a, ll b, ll m){
        ll res = 1;
        while(b > 0){
            if(b&1){
                res = mul(res, a);
            }
            b /= 2;
            a = mul(a, a);
        }
        return res%m;
    }
    int numSubseq(vector<int>& nums, int target) {
        sort(nums.begin(), nums.end());
        ll ans = 0;
        for(ll i = 0; i < nums.size(); i ++){
            ll ind = upper_bound(nums.begin(), nums.begin() + i, target - nums[i]) - nums.begin();
            ind --;
            if(2 * nums[i] <= target)ans++;
            ans += mul(power(2, ind + 1, MOD) - 1, power(2, i - ind - 1, MOD));
            ans %= MOD;
        }
        return ans%MOD;
    }
};
