class Solution {
public:

    int numSub(string s) {
        const int m = 1e9 + 7;
        long long ans = 0;
        long long cnt = 0;
        for(auto i : s){
            if(i == '1'){
                cnt++;
            }else{
                long long temp = ((cnt)%m *(cnt + 1)%m)%m;
                temp/=2;
                ans  = (ans%m + temp%m)%m;
                cnt = 0;
            }
        }
        long long temp = ((cnt)%m *(cnt + 1)%m)%m;
        temp/=2;
        ans  = (ans%m + temp%m)%m;
        return ans;
    }
};
