class Solution {
public:
    int helper(char *c, int ind)
    {
        if (ind == 4)
        {
            if((c[0]-'0') * 10 + (c[1]-'0') <=23 && (c[2]-'0') * 10 + (c[3]-'0') < 60 ){
                return 1;
            }else{
                return 0;
            }
        }
        if (c[ind] != '?')
        {
            return helper(c, ind + 1);
        }
        else
        {
            int ans = 0;
            char org = c[ind];
            for (int i = 0; i <= 9; i++)
            {
                c[ind] = '0' + i;
                ans += helper(c, ind + 1);
                c[ind] = org;
            }
            return ans;
        }
    }
    int countTime(string time) {
        char c[4];
        c[0] = time[0];
        c[1] = time[1];
        c[2] = time[3];
        c[3] = time[4];
        return helper(c, 0);
    }
};
