class Solution {
public:
int helper(int amt,int cost2){
    int x = amt/cost2;
    return x+1;
}
    long long waysToBuyPensPencils(int total, int cost1, int cost2) {
     int maxpens = total/cost1;
long long  ways = 0;
     for(int i =0;i<=maxpens;i++){
         ways+=helper(total-(cost1*i),cost2);

     }   
     return ways;
    }
};
